# -*-coding:utf-8 -*-def exec_ansible(module,args,host):    import json    from collections import namedtuple    from ansible.parsing.dataloader import DataLoader    from ansible.vars import VariableManager    from ansible.inventory import Inventory    from ansible.playbook.play import Play    from ansible.executor.task_queue_manager import TaskQueueManager    from ansible.plugins.callback import CallbackBase    class ResultCallback(CallbackBase):        def v2_runner_on_ok(self, result, **kwargs):            host = result._host            self.data = json.dumps({host.name: result._result}, indent=4)    Options = namedtuple('Options', ['connection', 'module_path', 'forks', 'become', 'become_method', 'become_user', 'check'])    variable_manager = VariableManager()    loader = DataLoader()    options = Options(connection='smart', module_path='/usr/lib/python2.7/site-packages/ansible/modules/', forks=100, become=None, become_method=None, become_user='root', check=False)    passwords = dict(vault_pass='secret')    results_callback = ResultCallback()    inventory = Inventory(loader=loader, variable_manager=variable_manager, host_list='/etc/ansible/hosts')    variable_manager.set_inventory(inventory)    play_source =  dict(            name = "Ansible Play",            hosts = host,            gather_facts = 'no',            tasks = [                dict(action=dict(module=module, args=args), register='shell_out'),             ]        )    play = Play().load(play_source, variable_manager=variable_manager, loader=loader)    tqm = None    try:        tqm = TaskQueueManager(                  inventory=inventory,                  variable_manager=variable_manager,                  loader=loader,                  options=options,                  passwords=passwords,                  stdout_callback=results_callback,              )        result = tqm.run(play)    finally:        if tqm is not None:            tqm.cleanup        return json.loads(results_callback.data)    